import React, { useEffect, useState } from "react";

import {
  HeaderContainer,
  NavLinks,
  Link,
  HamburgerMenu,
  LogoContainer,
  StyledNavLinks,
} from "./Header.styles";

import Menu from "../utils/Menu";
import { ReactComponent as Code } from "../../assets/icons/code.svg";

const Header = ({
  onAboutClick,
  onProjectsClick,
  setShowContact,
  onContactClick,
  showContact,
}) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isVisible, setIsVisible] = useState(menuOpen);
  const [animation, setAnimation] = useState(true);

  useEffect(() => {
    let timer;
    if (menuOpen) {
      setIsVisible(true);
    } else {
      timer = setTimeout(() => setIsVisible(false), 200);
    }

    return () => clearTimeout(timer);
  }, [menuOpen]);

  useEffect(() => {
    if (menuOpen) {
      document.documentElement.style.overflowY = "hidden";
    } else {
      document.documentElement.style.overflowY = "scroll";
    }
  }, [menuOpen]);

  return (
    <HeaderContainer $open={menuOpen}>
      <LogoContainer $invisible>
        <Code />
      </LogoContainer>
      <LogoContainer>
        <Code />
      </LogoContainer>

      <NavLinks>
        <Link
          onClick={() => {
            setShowContact(false);
            setAnimation(false);
            setMenuOpen(false);
            window.scrollTo(0, 0);
          }}
        >
          Home
        </Link>

        <Link
          onClick={() => {
            setShowContact(false);
            setAnimation(false);
            setMenuOpen(false);

            if (showContact) {
              setTimeout(() => {
                onAboutClick();
              }, 300);
            } else {
              onAboutClick();
            }
          }}
        >
          About
        </Link>

        <Link
          onClick={() => {
            setShowContact(false);
            setAnimation(false);
            setMenuOpen(false);
            if (showContact) {
              setTimeout(() => {
                onProjectsClick();
              }, 300);
            } else {
              onProjectsClick();
            }
          }}
        >
          Projects
        </Link>

        <Link
          onClick={() => {
            window.scrollTo(0, 0);
            onContactClick();
            setMenuOpen(false);
          }}
        >
          Contact
        </Link>
      </NavLinks>
      {isVisible && (
        <StyledNavLinks $open={menuOpen}>
          <Link
            onClick={() => {
              setShowContact(false);
              setAnimation(false);
              setMenuOpen(false);
              window.scrollTo(0, 0);
            }}
          >
            Home
          </Link>

          <Link
            onClick={() => {
              setShowContact(false);
              setAnimation(false);
              setMenuOpen(false);
              if (showContact) {
                setTimeout(() => {
                  onAboutClick();
                }, 300);
              } else {
                onAboutClick();
              }
            }}
          >
            About
          </Link>

          <Link
            onClick={() => {
              setShowContact(false);
              setAnimation(false);
              setMenuOpen(false);
              if (showContact) {
                setTimeout(() => {
                  onProjectsClick();
                }, 300);
              } else {
                onProjectsClick();
              }
            }}
          >
            Projects
          </Link>

          <Link
            onClick={() => {
              setMenuOpen(false);
              window.scrollTo(0, 0);
              onContactClick();
              setAnimation(false);
            }}
          >
            Contact
          </Link>
        </StyledNavLinks>
      )}
      <HamburgerMenu $open={menuOpen}>
        <Menu
          onClick={() => {
            setMenuOpen(!menuOpen);
            setAnimation(true);
          }}
          animation={animation}
        />
      </HamburgerMenu>
    </HeaderContainer>
  );
};

export default Header;
